sys_variable_value.value:
(function execute(inputs, outputs) {
    //set a default return value
    outputs.answer = '';
	outputs.reviewtype = '';

  	// get the review type (schedule)
    var reviewSchedule = gs.getProperty('x_sisy_manage_pe_0.review_process_schedule');

    outputs.reviewtype = new x_sisy_manage_pe_0.PRUtils().getPeriodName(reviewSchedule,true);

        //get today's date as a Date/Time object
        //get just the date (Drop time)
        //convert to milliseconds
        var today = new GlideDateTime().getDate().getNumericValue();

        //pull the date to kick off review from system properties
        //convert it to a Date/Time object
        var start = new GlideDateTime(gs.getProperty('x_sisy_manage_pe_0.employee_prompt_self_review'));

        //get just the date (drop the time) and convert to milliseconds
        var startDate = start.getDate().getNumericValue();

        //set the answer variable to the result of comparing the two millisecond values
        //if today is the start date, answer will now equal true
        //return the answer
        if (startDate == today) outputs.answer = 'prompt';

        //pull the date to kick off review from system properties
        //convert it to a Date/Time object
        var reminder = new GlideDateTime(gs.getProperty('x_sisy_manage_pe_0.notify_reminder'));

        //get just the date (drop the time) and convert to milliseconds
        var reminderDate = reminder.getDate().getNumericValue();

        //set the answer variable to the result of comparing the two millisecond values
        //if today is the reminder date 
        //return the answer
        if (reminderDate == today) outputs.answer = 'reminder';

        //pull the date to kick off review from system properties
        //convert it to a Date/Time object
        var confirm = new GlideDateTime(gs.getProperty('x_sisy_manage_pe_0.review_confirmation_deadline'));

        //get just the date (drop the time) and convert to milliseconds
        var confirmDate = confirm.getDate().getNumericValue();

        //set the answer variable to the result of comparing the two millisecond values
        //if today is the confirmation date 
        //return the answer
        if (confirmDate == today) outputs.answer = 'confirm';

	    //pull the date to kick off review from system properties
        //convert it to a Date/Time object
        var endDt = new GlideDateTime(gs.getProperty('x_sisy_manage_pe_0.end_date'));

        //get just the date (drop the time) and convert to milliseconds
        var endDate = endDt.getDate().getNumericValue();

        //set the answer variable to the result of comparing the two millisecond values
        //if today is the confirmation date 
        //return the answer
        if (endDate == today) outputs.answer = 'end';

})(inputs, outputs);

